package org.jvnet.hudson.crypto;

import org.junit.jupiter.api.Test;

import java.math.BigInteger;
import java.security.interfaces.RSAPublicKey;

import static org.junit.jupiter.api.Assertions.assertEquals;

/**
 * @author Kohsuke Kawaguchi
 */
class RSAPublicKeyUtilTest {
    @Test
    void testReadPublicKey() throws Exception {
        RSAPublicKey p = (RSAPublicKey) RSAPublicKeyUtil.readPublicKey("ssh-rsa  kohsuke@griffon.2013");
        System.out.println(p);

        assertEquals(new BigInteger("",16), p.getModulus());
        // this hex output I got from "openssl rsa -modulus -noout -in ~/.ssh/id_rsa"
        assertEquals(new BigInteger("65537"), p.getPublicExponent());
    }

    @Test
    void testGetFingerPrint() throws Exception {
        RSAPublicKey p = (RSAPublicKey) RSAPublicKeyUtil.readPublicKey("ssh-rsa  kohsuke@griffon.2013");
        assertEquals("f7:7a:42:76:79:e8:8a:1a:4a:32:0c:b3:f9:3b:53:d4",RSAPublicKeyUtil.getFingerPrint(p));
    }
}
